	private int checkBirthday() {
		if (birthday.trim().length() == 0) 
			return 1;
		String[] t = birthday.split("/");
		Calendar now = Calendar.getInstance();
		Calendar birth = Calendar.getInstance();
		birth.set(Integer.parseInt(t[2]), Integer.parseInt(t[1]) - 1, Integer.parseInt(t[0]));
		int result = now.compareTo(birth);
		System.out.println(result);
		if (result <= 0) {
			return 2;
		}
		return 0;
	}
